from iturtle import Turtle

turtle = Turtle()

turtle.speed(10)
turtle.bgcolor('black')

r, g, b = 255, 0, 0

for i in range(255):
    if i < 255//3:
        g += 3
    elif i < (255*2)//3:
        r -= 3
    elif i < 255:
        b += 3
    elif i < (255*4)//3:
        g -= 3
    elif i < (255*5)//3:
        r += 3
    else:
        b -= 3

    turtle.forward(50+i)
    turtle.right(91)
    turtle.pencolor(r, g, b)
